import { UseQueryOptions, useQuery } from "@tanstack/react-query";
import { buildRequest } from "./cda";

// Return types generated by https://app.quicktype.io/
interface TimeSeries {
  begin: string;
  end: string;
  interval: string;
  "interval-offset": number;
  name: string;
  "office-id": string;
  page: string;
  "page-size": number;
  "time-zone": string;
  total: number;
  units: string;
  "value-columns": ValueColumn[];
  values: Array<number[]>;
  "vertical-datum-info": VerticalDatumInfo;
}

interface ValueColumn {
  name: string;
  ordinal: number;
  datatype: string;
}

interface VerticalDatumInfo {
  office: string;
  unit: string;
  location: string;
  "native-datum": string;
  elevation: number;
  offsets: Offset[];
}

interface Offset {
  estimate: boolean;
  "to-datum": string;
  value: number;
}

type GetTimeSeriesParams = {
  name: string;
  office: string;
  unit?: string;
  datum?: string;
  begin?: string;
  end?: string;
  timezone?: string;
  format?: string;
};

const getTimeSeries = async (
  params: GetTimeSeriesParams,
  cdaUrl?: string
): Promise<TimeSeries> => {
  const paramString = new URLSearchParams(params).toString();
  const url = buildRequest("/timeseries", paramString, cdaUrl);
  const response = await fetch(url, {
    headers: {
      accept: "application/json;version=2",
    },
  });
  if (!response.ok) {
    throw new Error("Error retrieving data.");
  }
  return response.json();
};

interface useTimeSeriesParams {
  params: GetTimeSeriesParams;
  cdaUrl?: string;
  queryOptions?: Omit<UseQueryOptions<TimeSeries>, "queryKey" | "queryFn">;
}

const useTimeSeries = ({
  params,
  cdaUrl,
  queryOptions,
}: useTimeSeriesParams) => {
  const queryKey = ["cda", "timeseries", params.name];
  if (params.begin) queryKey.push(`begin: ${params.begin}`);
  if (params.end) queryKey.push(`end: ${params.end}`);
  return useQuery({
    queryKey: queryKey,
    queryFn: async () => {
      return getTimeSeries(params, cdaUrl);
    },
    ...queryOptions,
  });
};

export { useTimeSeries };
export default useTimeSeries;
